mapper.select:
SELECT tb3.blog_id,tb3.blog_title,tb3.blog_content,tb3.create_time,tb3.name,tb3.type_name,tb3.cnumber,tb3.bnumber,tb3.user_id from(SELECT * from (SELECT b.*,u.name,bt.type_name,u.user_id from bloginfo b,userinfo u,blog_type bt,user_blog ub WHERE b.blog_id = ub.blog_id and ub.user_id = u.user_id and b.bt_id = bt.bt_id
and bt.type_name = #{type_name}
and b.`status`=1 ) tb1 LEFT JOIN (SELECT c.blog_id bid ,COUNT(c.blog_id) as cnumber from comment1 c,blog_type bt,bloginfo b where b.blog_id = c.blog_id
and bt.type_name = #{type_name}
and b.bt_id = bt.bt_id GROUP BY bid) tb2 on tb1.blog_id = tb2.bid LEFT JOIN (SELECT br.blog_id bid2,COUNT(DISTINCT br.user_id) as bnumber from browse br,blog_type bt,bloginfo b where b.blog_id = br.blog_id
and bt.type_name = #{type_name}
and b.bt_id = bt.bt_id GROUP BY br.blog_id) tb4 on tb1.blog_id = tb4.bid2) tb3

SELECT ss.type_name ,ii.* from (
 SELECT  pp.pt_id ,count(pp.pt_id) as wznumber from (
 SELECT pt.*,pm.blog_id from person_type pt LEFT JOIN pbt_middle pm ON pt.pt_id=pm.pt_id  and pm.`status`=1
 where pt.user_id= #{user_id}
 ) pp GROUP BY pp.pt_id) ii  , person_type ss   where  ss.pt_id = ii.pt_id
